import React, { useState } from 'react';
import {
    VerticalTimeline, VerticalTimelineElement,
} from "react-vertical-timeline-component";
import "react-vertical-timeline-component/style.min.css";
import SchoolIcon from '@mui/icons-material/School';
import WorkIcon from '@mui/icons-material/Work';
import LinkedInIcon from '@mui/icons-material/LinkedIn';
import "../styles/Experience.css";
import { Button, TextField, Box } from '@mui/material';

function Experience() {
  const [formData, setFormData] = useState({ name: "", email: "", message: "" });


  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert("Thank you for reaching out! We will get back to you soon.");
    setFormData({ name: "", email: "", message: "" });
  };

  const handleLinkedInRedirect = () => {
    window.open("https://www.linkedin.com/in/your-profile", "_blank");
  };

  return (
    <div className="experience">
      <h5>P.A.V.A.N. - Professional Achievements and Versatile Artistic Narrative</h5>
      <VerticalTimeline lineColor="#3e497a">
        <VerticalTimelineElement
          className="vertical-timeline-element--work"
          date="2023 - present"
          iconStyle={{ background: "#e9d35b", color: "#fff" }}
          icon={<WorkIcon />}
        >
          <h3 className="vertical-timeline-element-title">
            Software Engineer 2 - Radiometer
          </h3>
          <h4 className="vertical-timeline-element-subtitle">Bengaluru, KA</h4>
          <p style={{ textAlign: "justify" }}>
            <ul>
              <li>
                Developed a <strong>Kaplan automation tool</strong>, utilizing data preprocessing and statistical techniques to generate <strong>Master Calibration Curves (MCC)</strong> from CSV datasets, ensuring accurate assay calibration through background correction and data validation.
              </li>
              <li>
                Built an <strong>automation tool</strong> that reduced validation time for <strong>800+ work items</strong> from days to minutes, significantly improving efficiency and saving resources in agile sprints.
              </li>
              <li>
                Gained hands-on experience with <strong>Microsoft Azure</strong> for CI/CD pipeline management, learned <strong>Docker</strong> for containerization, and utilized <strong>Git</strong> for version control and collaborative development.
              </li>
            </ul>
          </p>
        </VerticalTimelineElement>

        <VerticalTimelineElement
          className="vertical-timeline-element--work"
          date="Nov 2022 - Oct 2023"
          iconStyle={{ background: "#e9d35b", color: "#fff" }}
          icon={<WorkIcon />}
        >
          <h3 className="vertical-timeline-element-title">
            Software Engineer Intern - Radiometer
          </h3>
          <h4 className="vertical-timeline-element-subtitle">Bengaluru, KA</h4>
        </VerticalTimelineElement>

        <VerticalTimelineElement
          className="vertical-timeline-element--education"
          date="2021 - present"
          iconStyle={{ background: "#3e497a", color: "#fff" }}
          icon={<SchoolIcon />}
        >
          <h3 className="vertical-timeline-element-title">
            MS Ramaiah Institute of Technology, Bengaluru, Karnataka.
          </h3>

          <h4 className="vertical-timeline-element-subtitle">
            Master's Degree
          </h4>

          <p> Computer Science and Engineering</p>
        </VerticalTimelineElement>

        <VerticalTimelineElement
          className="vertical-timeline-element--work"
          date="2018 - 2019"
          iconStyle={{ background: "#e9d35b", color: "#fff" }}
          icon={<WorkIcon />}
        >
          <h3 className="vertical-timeline-element-title">
            Database Administrator - Tata Consultancy Services.
          </h3>
          <h4 className="vertical-timeline-element-subtitle">Bengaluru, KA</h4>
        </VerticalTimelineElement>

        <VerticalTimelineElement
          className="vertical-timeline-element--education"
          date="2014 - 2018"
          iconStyle={{ background: "#3e497a", color: "#fff" }}
          icon={<SchoolIcon />}
        >
          <h3 className="vertical-timeline-element-title">
            PES College of Engineering, Mandya, Karnataka.
          </h3>

          <h4 className="vertical-timeline-element-subtitle">
            Bachelor's Degree in Computer Science and Engineering
          </h4>
        </VerticalTimelineElement>

        <VerticalTimelineElement
          className="vertical-timeline-element--education"
          date="2012 - 2014"
          iconStyle={{ background: "#3e497a", color: "#fff" }}
          icon={<SchoolIcon />}
        >
          <h3 className="vertical-timeline-element-title">
            Alva's PU College, Moodabidri, Karnataka.
          </h3>

          <h4 className="vertical-timeline-element-subtitle">10+2(PUC) - PCMC</h4>
        </VerticalTimelineElement>

        <VerticalTimelineElement
          className="vertical-timeline-element--education"
          date="2002 - 2012"
          iconStyle={{ background: "#3e497a", color: "#fff" }}
          icon={<SchoolIcon />}
        >
          <h3 className="vertical-timeline-element-title">
            Pushpa High School, Periyapatna, Karnataka.
          </h3>
          <p> High School</p>
        </VerticalTimelineElement>
      </VerticalTimeline>

      <Box className="contact-form" p={2} mt={4} border={1} borderRadius={2} borderColor="#3e497a">
        <h2>Contact Me</h2>
        <form onSubmit={handleSubmit}>
          <TextField
            fullWidth
            margin="normal"
            label="Name"
            name="name"
            value={formData.name}
            onChange={handleInputChange}
            required
          />
          <TextField
            fullWidth
            margin="normal"
            label="Email"
            type="email"
            name="email"
            value={formData.email}
            onChange={handleInputChange}
            required
          />
          <TextField
            fullWidth
            margin="normal"
            label="Message"
            name="message"
            value={formData.message}
            onChange={handleInputChange}
            multiline
            rows={4}
            required
          />
          <Button type="submit" variant="contained" color="primary">
            Submit
          </Button>
        </form>
      </Box>

      <Box mt={4} textAlign="center">
        <Button
          variant="contained"
          color="primary"
          startIcon={<LinkedInIcon />}
          onClick={handleLinkedInRedirect}
        >
          Connect with LinkedIn
        </Button>
      </Box>
    </div>
  );
}

export default Experience;
