import React from "react";
import { VerticalTimeline, VerticalTimelineElement } from "react-vertical-timeline-component";
import "react-vertical-timeline-component/style.min.css";
import WorkIcon from "@mui/icons-material/Work";
import SchoolIcon from '@mui/icons-material/School';

const timelineData = [
  {
    title: "Software Engineer 2 - Radiometer",
    date: "2023 - present",
    subtitle: "Bengaluru, KA",
    description: "Working on C#.NET, WPF, and Python. Developing and maintaining advanced immunoassay device software solutions.",
    icon: <WorkIcon />,
    iconStyle: { background: "#e9d35b", color: "#fff" },
  },
  {
    title: "Software Engineer Intern - Radiometer",
    date: "Nov 2022 - Oct 2023",
    subtitle: "Bengaluru, KA",
    description:
      "Collaborated on machine learning projects for log analysis. Assisted in transitioning WPF applications to Blazor desktop using Generative AI.",
    icon: <WorkIcon />,
    iconStyle: { background: "#e9d35b", color: "#fff" },
  },
  {
    title: "Full Stack Intern - XYZ Solutions",
    date: "May 2022 - Oct 2022",
    subtitle: "Remote",
    description:
      "Built end-to-end web applications using React and Node.js. Designed APIs and optimized database queries for scalable performance.",
    icon: <WorkIcon />,
    iconStyle: { background: "#3e497a", color: "#fff" },
  },
  {
    title: "Freelance Developer",
    date: "Jan 2021 - Apr 2022",
    subtitle: "Remote",
    description:
      "Developed custom software solutions for small businesses. Delivered projects in Python, JavaScript, and cloud platforms.",
    icon: <SchoolIcon />,
    iconStyle: { background: "#1e88e5", color: "#fff" },
  },
  {
    title: "Trainee - ABC Tech",
    date: "Aug 2020 - Dec 2020",
    subtitle: "Bengaluru, KA",
    description: "Gained hands-on experience in front-end and back-end technologies during a 4-month intensive training program.",
    icon: <WorkIcon />,
    iconStyle: { background: "#4caf50", color: "#fff" },
  },
];

const TimelineElement = ({ date, title, subtitle, description, icon, iconStyle }) => (
  <VerticalTimelineElement date={date} iconStyle={iconStyle} icon={icon}>
    <h3 className="vertical-timeline-element-title">{title}</h3>
    <h4 className="vertical-timeline-element-subtitle">{subtitle}</h4>
    <p>{description}</p>
  </VerticalTimelineElement>
);

const Experience = () => {
  return (
    <div className="experience">
      <VerticalTimeline lineColor="#3e497a">
        {timelineData.map((item, index) => (
          <TimelineElement
            key={index}
            date={item.date}
            title={item.title}
            subtitle={item.subtitle}
            description={item.description}
            icon={item.icon}
            iconStyle={item.iconStyle}
          />
        ))}
      </VerticalTimeline>
    </div>
  );
};

export default Experience;
